                    gridControlNguoiKy.DataSource = db.NguoiKies.ToList();
                    gridControlNguoiKy.RefreshDataSource();
                    return false;
                });
                //fthemsuangky.MdiParent = this.MdiParent;
                fthemsuangky.Show();
            }
            else
            {
                ActiveChildForm("frmThemNguoiKy");
            }
        }


